CREATE TABLE IF NOT EXISTS Journey(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR, start TIMESTAMP NOT NULL, end TIMESTAMP NOT NULL);
CREATE TABLE IF NOT EXISTS Place(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, city VARCHAR NOT NULL, country VARCHAR NOT NULL, latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL, journey_id INTEGER );
CREATE TABLE IF NOT EXISTS Photographer(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Tag(id INTEGER GENERATED BY DEFAULT  AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Photo(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, photographer_id INTEGER, path VARCHAR NOT NULL, rating INTEGER NOT NULL DEFAULT 0, datetime TIMESTAMP NOT NULL, latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL, place_id INTEGER, journey_id INTEGER);
CREATE TABLE IF NOT EXISTS PhotoTag(photo_id INTEGER REFERENCES Photo(id), tag_id INTEGER REFERENCES Tag(id), PRIMARY KEY(photo_id, tag_id));
CREATE TABLE IF NOT EXISTS Slideshow(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(30), durationBetweenPhotos INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS Slide(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, slideshow_id INTEGER REFERENCES Slideshow(id), orderposition INTEGER NOT NULL, caption VARCHAR);
CREATE TABLE IF NOT EXISTS PhotoSlide(id INTEGER, FOREIGN KEY(id) REFERENCES Slide(id), photo_id INTEGER REFERENCES Photo(id));
CREATE TABLE IF NOT EXISTS MapSlide(id INTEGER, FOREIGN KEY(id) REFERENCES Slide(id), latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL);
CREATE TABLE IF NOT EXISTS TitleSlide(id INTEGER, FOREIGN KEY(id) REFERENCES Slide(id), color INTEGER(10));



