CREATE TABLE IF NOT EXISTS Photographer(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Tag(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Photo(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, photographer_id INTEGER REFERENCES Photographer(id), path VARCHAR NOT NULL, rating INTEGER NOT NULL DEFAULT 0);
CREATE TABLE IF NOT EXISTS PhotoTag(photo_id INTEGER REFERENCES Photo(id), tag_id INTEGER REFERENCES Tag(id), PRIMARY KEY(photo_id, tag_id));
CREATE TABLE IF NOT EXISTS Exif(photo_id INTEGER NOT NULL, date TIMESTAMP NOT NULL, exposure VARCHAR NOT NULL, aperture DOUBLE NOT NULL, focallength DOUBLE NOT NULL, iso INTEGER NOT NULL, flash BOOLEAN NOT NULL, make VARCHAR NOT NULL, model VARCHAR NOT NULL, latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL, altitude DOUBLE NOT NULL);
