CREATE TABLE IF NOT EXISTS Photographer(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Tag(id INTEGER GENERATED BY DEFAULTh2  AS IDENTITY PRIMARY KEY, name VARCHAR(30));
CREATE TABLE IF NOT EXISTS Photo(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, photographer_id INTEGER REFERENCES Photographer(id), path VARCHAR NOT NULL, rating INTEGER NOT NULL DEFAULT 0, datetime TIMESTAMP NOT NULL, latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL, place VARCHAR REFERENCES place(city), journey VARCHAR REFERENCES journey(name);
CREATE TABLE IF NOT EXISTS PhotoTag(photo_id INTEGER REFERENCES Photo(id), tag_id INTEGER REFERENCES Tag(id), PRIMARY KEY(photo_id, tag_id));
CREATE TABLE IF NOT EXISTS Journey(name VARCHAR PRIMARY KEY, start TIMESTAMP NOT NULL, end TIMESTAMP NOT NULL);
CREATE TABLE IF NOT EXISTS Place(city VARCHAR PRIMARY KEY, country VARCHAR NOT NULL);